import React, { useState } from 'react';

const Projetos = () => {
  const [activeProject, setActiveProject] = useState(null);

  const toggleProject = (project) => {
    setActiveProject(activeProject === project ? null : project);
  };

  const projects = [
    {
      id: 'nectopoint',
      title: 'NectoPoint - Sistema de Ponto Eletrônico',
      description: (
        <>
          <p className="mb-4">
            Neste projeto, atuei como desenvolvedor frontend em um sistema completo de gestão de ponto eletrônico. O backend foi desenvolvido em Java pela equipe, enquanto meu foco foi no frontend utilizando React com TypeScript.
          </p>
          <p>
            
          </p>
        </>
      ),
      details: [
        'Desenvolvimento das interfaces de usuário com React e TypeScript',
        'Criação dos formulários e fluxos para solicitações de ajustes de ponto',
        'Implementação do sistema de aprovação de solicitações para gerentes',
        'Design e desenvolvimento das navbars diferenciadas para cada tipo de usuário',
        'Integração com a API REST desenvolvida em Java pelo time de backend',
        'Garantia de usabilidade e experiência intuitiva em todas as telas'
      ],
      technologies: ['React', 'TypeScript', 'Java', 'UI/UX Design', 'Spring Boot'],
      githubLink: 'https://github.com/Equipe-Skyfall/nectopoint',
      softSkills: [
        {
          name: 'Proatividade',
          stars: 9,
          description: 'Fui proativo em buscar tasks sem necessidade de cobrança, sempre antecipando as próximas etapas do desenvolvimento. Assumi responsabilidades extras como a documentação dos componentes para facilitar a manutenção futura.'
        },
        {
          name: 'Comunicação',
          stars: 8,
          description: 'Mantive comunicação constante com a equipe de backend, seja para pedir ajuda quando necessário ou para oferecer suporte nas integrações. Isso garantiu que todos estivessem alinhados sobre o progresso do projeto.'
        },
        {
          name: 'Trabalho em Equipe',
          stars: 9,
          description: 'Colaborei ativamente com outros desenvolvedores, revisando códigos e compartilhando conhecimento sobre TypeScript, que era novidade para parte da equipe. Essa troca foi essencial para a qualidade final do produto.'
        }
      ]
    },
    {
      id: 'prostock',
      title: 'ProStock - Sistema de Gestão de Estoque',
      description: (
        <>
          <p className="mb-4">
            Neste projeto, atuei como master da equipe, com o objetivo de desenvolver um programa de gerenciamento de estoque. A solução incluiu a implementação de um sistema completo de CRUD, integrado ao backend, desenvolvido em Prisma e JavaScript, e ao frontend, desenvolvido em TypeScript com React.
          </p>
          <p>
            Como líder técnico, estabeleci processos ágeis que aumentaram a produtividade em 30%. A visão sistêmica do projeto permitiu criar uma arquitetura escalável que atendia tanto às necessidades imediatas quanto a possíveis expansões futuras do sistema.
          </p>
        </>
      ),
      details: [
        'Liderança técnica como master da equipe',
        'Desenvolvimento completo do sistema CRUD',
        'Integração entre frontend (TypeScript/React) e backend (JavaScript/Prisma)',
        'Criação de interfaces intuitivas e funcionais',
        'Otimização de performance para gestão eficiente de estoque',
        'Implementação de testes automatizados para componentes críticos'
      ],
      technologies: ['React', 'TypeScript', 'Prisma', 'REST API'],
      githubLink: 'https://github.com/EquipeSkyfall/API_2Semestre',
      softSkills: [
        {
          name: 'Liderança',
          stars: 9,
          description: 'Gerenciei a equipe distribuindo tasks de forma equilibrada, acompanhando o progresso diário e removendo obstáculos. Estabeleci reuniões curtas e objetivas que mantinham todos focados nas metas do sprint.'
        },
        {
          name: 'Organização',
          stars: 10,
          description: 'Planejei a arquitetura do sistema e dividi o projeto em módulos lógicos, o que permitiu desenvolvimento paralelo pela equipe. Criei documentação detalhada que acelerou a integração entre frontend e backend.'
        },
        {
          name: 'Visão Sistêmica',
          stars: 8,
          description: 'Antecipei necessidades futuras do sistema, como a possibilidade de expansão para múltiplos armazéns, e incorporei essa flexibilidade na arquitetura desde o início, economizando retrabalho posterior.'
        }
      ]
    },
    {
      id: 'estufa',
      title: 'Smart Farming - Monitoramento de Estufas',
      description: (
        <>
          <p className="mb-4">
            Neste projeto, contribuí com o desenvolvimento da arquitetura de informação e o design das interfaces do site. Também trabalhei na integração de containers de plataformas de execução para criar uma estufa virtual inteligente.
          </p>
          <p>
            A autonomia foi crucial para entregar rapidamente as interfaces de monitoramento dos sensores. A simplicidade relativa do projeto me permitiu explorar soluções criativas de visualização de dados que melhoraram significativamente a experiência do usuário final.
          </p>
        </>
      ),
      details: [
        'Arquitetura de informação e design de interfaces',
        'Integração de containers para estufa virtual',
        'Controle e análise automatizada de dados de sensores',
        'Desenvolvimento de interface amigável com gráficos',
        'Otimização da visualização de dados para eficiência operacional',
        'Criação de um sistema de alertas baseado em thresholds'
      ],
      technologies: ['IoT', 'UI/UX Design', 'Data Visualization', 'Python', 'Docker'],
      githubLink: 'https://github.com/CyberScrums/Projeto-Smart-Farming',
      softSkills: [
        {
          name: 'Autonomia',
          stars: 10,
          description: 'Trabalhei de forma independente, tomando decisões de design e implementação sem necessidade de supervisão constante. Pesquisei e implementei bibliotecas de gráficos que atenderam perfeitamente às necessidades do projeto.'
        },
        {
          name: 'Criatividade',
          stars: 8,
          description: 'Desenvolvi visualizações inovadoras para os dados dos sensores, transformando informações complexas em dashboards intuitivos que os agricultores poderiam entender facilmente.'
        },
        {
          name: 'Eficiência',
          stars: 9,
          description: 'Otimizei meu fluxo de trabalho para entregar todas as interfaces dentro do prazo, mesmo trabalhando em outras tarefas paralelamente. Priorizei as funcionalidades que traziam maior valor para o usuário final.'
        }
      ]
    }
  ];

  return (
    <section className="py-20 px-4 sm:px-6 max-w-6xl mx-auto">
      <div className="text-center mb-12 sm:mb-16">
        <div className="relative inline-block group">
          <h1 className="text-3xl sm:text-4xl md:text-5xl font-bold text-amber-400 mb-4">
            Meus Projetos
          </h1>
        </div>
        <p className="text-lg sm:text-xl text-gray-300 max-w-3xl mx-auto px-4">
          Trabalhos onde combinei habilidades técnicas e visão de produto para entregar soluções completas
        </p>
      </div>

      <div className="space-y-6 sm:space-y-8 px-2 sm:px-0">
        {projects.map((project) => (
          <div
            key={project.id}
            className="bg-gray-800/50 backdrop-blur-sm border border-gray-700 rounded-xl overflow-hidden transition-all duration-300 hover:border-amber-400/30"
          >
            <div className="p-6 sm:p-8">
              <div className="flex flex-col md:flex-row md:items-start justify-between gap-6">
                <div className="flex-1">
                  <h2 className="text-xl sm:text-2xl md:text-3xl font-bold text-amber-400 mb-2">{project.title}</h2>

                  <div className="flex flex-wrap gap-2 mb-4">
                    {project.technologies.map((tech, index) => (
                      <span
                        key={index}
                        className="text-xs sm:text-sm px-3 py-1 bg-gray-700 rounded-full text-amber-300"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>

                  <div className="text-gray-300 mb-4 space-y-4 text-sm sm:text-base">
                    {typeof project.description === 'string' ? (
                      <p>{project.description}</p>
                    ) : (
                      project.description
                    )}
                  </div>

                  {activeProject === project.id && (
                    <div className="mt-6 space-y-6">
                      <div>
                        <h3 className="text-lg font-semibold text-amber-300 mb-3">Contribuições Pessoais</h3>
                        <div className="space-y-3 pl-4 border-l-2 border-amber-400/30 text-sm sm:text-base">
                          {project.details.map((item, index) => (
                            <div key={index} className="text-gray-300">
                              <p className="mb-2">• {item}</p>
                            </div>
                          ))}
                        </div>
                      </div>

                      <div>
                        <h3 className="text-lg font-semibold text-amber-300 mb-3">Soft Skills Demonstradas</h3>
                        <div className="space-y-4">
                          {project.softSkills.map((skill, index) => (
                            <div key={index} className="bg-gray-700/50 p-4 rounded-lg">
                              <div className="flex justify-between items-center mb-1">
                                <h4 className="font-medium text-amber-200">{skill.name}</h4>
                              </div>
                              <p className="text-gray-300 text-sm mt-1">{skill.description}</p>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  )}
                </div>

                <div className="flex flex-col space-y-3 sm:space-y-4 w-full sm:w-48">
                  <button
                    onClick={() => toggleProject(project.id)}
                    className="px-4 sm:px-6 py-2 sm:py-3 bg-gradient-to-r from-amber-500/90 to-amber-600/90 rounded-lg font-medium hover:from-amber-600 hover:to-amber-700 transition-all duration-300 shadow-lg hover:shadow-xl text-sm sm:text-base"
                  >
                    {activeProject === project.id ? 'Mostrar Menos' : 'Detalhes'}
                  </button>

                  <a
                    href={project.githubLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="px-4 sm:px-6 py-2 sm:py-3 border-2 border-amber-400 text-amber-400 rounded-lg font-medium hover:bg-amber-400/10 transition-all duration-300 text-center text-sm sm:text-base"
                  >
                    Ver no GitHub
                  </a>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projetos;
