import React, { useState } from 'react';

const Projetos = () => {
  const [activeProject, setActiveProject] = useState(null);

  const toggleProject = (project) => {
    setActiveProject(activeProject === project ? null : project);
  };

  const projects = [
    {
      id: 'nectopoint',
      title: 'NectoPoint - Sistema de Ponto Eletrônico',
      description: (
        <>
          <p className="mb-4">
            Desenvolvi o frontend de um sistema de gestão de ponto eletrônico com React e TypeScript. O backend foi criado em Java pela equipe.
          </p>
          <p>
            Prototipei e implementei o site. Desenvolvi o sistema de solicitações para férias, folgas, abonos e ajustes de ponto. Integraei-o à API em Java. Aprimorei minhas competências em TypeScript e Java.
          </p>
        </>
      ),
      details: [
        'Desenvolvi interfaces com React e TypeScript.',
        'Implementei solicitações de férias, folgas, abonos e ajustes.',
        'Criei o sistema de aprovação para gerentes.',
        'Desenhei navbars específicas por usuário.',
        'Conectei o frontend à API REST em Java.',
        'Escrevi código organizado e documentado.'
      ],
      technologies: ['React', 'TypeScript', 'Java', 'UI/UX Design', 'Spring Boot'],
      githubLink: 'https://github.com/Equipe-Skyfall/nectopoint',
      softSkills: [
        {
          name: 'Proatividade',
          description: 'Aprimorei minha iniciativa ao longo do projeto. Comecei entregando tarefas com atraso. Posteriormente passei a concluí-las de forma autônoma.'
        },
        {
          name: 'Comunicação',
          description: 'Tive dificuldades para me comunicar com a equipe de backend e o grupo. Limitei minha interação a reuniões e colaborações pontuais.'
        },
        {
          name: 'Trabalho em Equipe',
          description: 'Enfrentei desafios iniciais no trabalho em equipe. Com o tempo melhorei. Ajudei colegas e busquei suporte quando necessário.'
        }
      ]
    },
    {
      id: 'prostock',
      title: 'ProStock - Sistema de Gestão de Estoque',
      description: (
        <>
          <p className="mb-4">
            Liderei o desenvolvimento de um sistema de gestão de estoque. Implantei um sistema CRUD completo. Utilizei TypeScript e React no frontend. O backend foi feito em JavaScript com Prisma.
          </p>
          <p>
            Adotei processos ágeis e aumentei a produtividade em 30%. Desenhei uma arquitetura escalável para suportar expansões futuras.
          </p>
        </>
      ),
      details: [
        'Gerenciei a equipe como líder técnico.',
        'Desenvolvi o sistema CRUD completo.',
        'Integraei frontend e backend.',
        'Projetei interfaces intuitivas.',
        'Otimizei o desempenho do sistema.',
        'Implementei testes automatizados.'
      ],
      technologies: ['React', 'TypeScript', 'Prisma', 'REST API'],
      githubLink: 'https://github.com/EquipeSkyfall/API_2Semestre',
      softSkills: [
        {
          name: 'Liderança',
          description: 'Coordenei a equipe e distribuí tarefas. Monitorei o progresso diário. Realizei reuniões objetivas para atingir metas.'
        },
        {
          name: 'Organização',
          description: 'Estruturei o sistema em módulos lógicos. Elaborei documentação detalhada para agilizar a integração.'
        },
        {
          name: 'Visão Sistêmica',
          description: 'Previ demandas futuras e projetei uma arquitetura flexível. Evitei retrabalho com planejamento inicial.'
        }
      ]
    },
    {
      id: 'estufa',
      title: 'Smart Farming - Monitoramento de Estufas',
      description: (
        <>
          <p className="mb-4">
            Colaborei na arquitetura de informação e no design de interfaces. Integraei containers para criar uma estufa virtual inteligente.
          </p>
          <p>
            Trabalhei de forma autônoma e entreguei interfaces de monitoramento rapidamente. Explorei visualizações criativas de dados para melhorar a experiência do usuário.
          </p>
        </>
      ),
      details: [
        'Estruturei a arquitetura de informação.',
        'Projetei interfaces do site.',
        'Integrei containers à estufa virtual.',
        'Automatizei análise de dados de sensores.',
        'Desenvolvi gráficos para visualização.',
        'Criei alertas baseados em limites.'
      ],
      technologies: ['IoT', 'UI/UX Design', 'Data Visualization', 'Python', 'Docker'],
      githubLink: 'https://github.com/CyberScrums/Projeto-Smart-Farming',
      softSkills: [
        {
          name: 'Autonomia',
          description: 'Atuei de forma independente em design e implementação. Selecionei bibliotecas de gráficos adequadas ao projeto.'
        },
        {
          name: 'Criatividade',
          description: 'Desenhei visualizações inovadoras de dados. Transformei informações complexas em dashboards claros.'
        },
        {
          name: 'Eficiência',
          description: 'Organizei meu trabalho para cumprir prazos. Priorizei funcionalidades de maior valor ao usuário.'
        }
      ]
    }
  ];

  return (
    <section className="py-20 px-4 sm:px-6 max-w-6xl mx-auto">
      <div className="text-center mb-12 sm:mb-16">
        <div className="relative inline-block group">
          <h1 className="text-3xl sm:text-4xl md:text-5xl font-bold text-amber-400 mb-4">
            Meus Projetos
          </h1>
        </div>
        <p className="text-lg sm:text-xl text-gray-300 max-w-3xl mx-auto px-4">
          Projetos que desenvolvi com habilidades técnicas e visão de produto
        </p>
      </div>

      <div className="space-y-6 sm:space-y-8 px-2 sm:px-0">
        {projects.map((project) => (
          <div
            key={project.id}
            className="bg-gray-800/50 backdrop-blur-sm border border-gray-700 rounded-xl overflow-hidden transition-all duration-300 hover:border-amber-400/30"
          >
            <div className="p-6 sm:p-8">
              <div className="flex flex-col md:flex-row md:items-start justify-between gap-6">
                <div className="flex-1">
                  <h2 className="text-xl sm:text-2xl md:text-3xl font-bold text-amber-400 mb-2">{project.title}</h2>

                  <div className="flex flex-wrap gap-2 mb-4">
                    {project.technologies.map((tech, index) => (
                      <span
                        key={index}
                        className="text-xs sm:text-sm px-3 py-1 bg-gray-700 rounded-full text-amber-300"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>

                  <div className="text-gray-300 mb-4 space-y-4 text-sm sm:text-base">
                    {project.description}
                  </div>

                  {activeProject === project.id && (
                    <div className="mt-6 space-y-6">
                      <div>
                        <h3 className="text-lg font-semibold text-amber-300 mb-3">Contribuições Pessoais</h3>
                        <div className="space-y-3 pl-4 border-l-2 border-amber-400/30 text-sm sm:text-base">
                          {project.details.map((item, index) => (
                            <div key={index} className="text-gray-300">
                              <p className="mb-2">• {item}</p>
                            </div>
                          ))}
                        </div>
                      </div>

                      <div>
                        <h3 className="text-lg font-semibold text-amber-300 mb-3">Soft Skills Demonstradas</h3>
                        <div className="space-y-4">
                          {project.softSkills.map((skill, index) => (
                            <div key={index} className="bg-gray-700/50 p-4 rounded-lg">
                              <div className="flex justify-between items-center mb-1">
                                <h4 className="font-medium text-amber-200">{skill.name}</h4>
                              </div>
                              <p className="text-gray-300 text-sm mt-1">{skill.description}</p>
                            </div>
                          ))}
                        </div>
                      </div>
                    </div>
                  )}
                </div>

                <div className="flex flex-col space-y-3 sm:space-y-4 w-full sm:w-48">
                  <button
                    onClick={() => toggleProject(project.id)}
                    className="px-4 sm:px-6 py-2 sm:py-3 bg-gradient-to-r from-amber-500/90 to-amber-600/90 rounded-lg font-medium hover:from-amber-600 hover:to-amber-700 transition-all duration-300 shadow-lg hover:shadow-xl text-sm sm:text-base"
                  >
                    {activeProject === project.id ? 'Mostrar Menos' : 'Detalhes'}
                  </button>

                  <a
                    href={project.githubLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="px-4 sm:px-6 py-2 sm:py-3 border-2 border-amber-400 text-amber-400 rounded-lg font-medium hover:bg-amber-400/10 transition-all duration-300 text-center text-sm sm:text-base"
                  >
                    Ver no GitHub
                  </a>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projetos;
