import React, { useState } from 'react';

const Projetos = () => {
  const [activeProject, setActiveProject] = useState(null);

  const toggleProject = (project) => {
    setActiveProject(activeProject === project ? null : project);
  };

  const projects = [
    {
      id: 'nectopoint',
      title: 'NectoPoint - Sistema de Ponto Eletrônico',
      description: (
        <>
          <p className="mb-4">
            Neste projeto, atuei como desenvolvedor frontend em um sistema de gestão de ponto eletrônico. Confesso que no início subestimei a complexidade da integração entre frontend (React/TypeScript) e backend (Java/Spring), o que me causou alguns percalços.
          </p>
          <p>
            Um dos maiores desafios foi implementar o sistema de solicitações (férias, folgas, ajustes de ponto) que precisava se comunicar perfeitamente com a API Java. Tive dificuldades iniciais com a tipagem no TypeScript e com a estrutura do Spring Boot, mas esses obstáculos se tornaram oportunidades para aprofundar meus conhecimentos.
          </p>
        </>
      ),
      details: [
        'Desenvolvimento das interfaces com React e TypeScript (aprendi na prática)',
        'Sistema de solicitações complexo que exigiu várias refatorações',
        'Dificuldades iniciais na integração com Java/Spring que foram superadas',
        'Aprendizado acelerado de Tailwind CSS durante o projeto',
        'Desafio de criar navbars específicas para cada tipo de usuário',
        'Código evoluiu de simples para bem estruturado ao longo do tempo'
      ],
      technologies: ['React', 'TypeScript', 'Java', 'UI/UX Design', 'Spring Boot'],
      githubLink: 'https://github.com/Equipe-Skyfall/nectopoint',
      softSkills: [
        {
          name: 'Resiliência',
          description: 'Tive várias noites frustrantes tentando fazer a integração funcionar, mas persisti até dominar os conceitos necessários. Errei muito, mas cada erro foi uma lição valiosa.'
        },
        {
          name: 'Aprendizado Contínuo',
          description: 'Admito que no começo não sabia Java suficiente para entender o backend, mas me forcei a aprender o básico para poder me comunicar melhor com a equipe e fazer integrações mais eficientes.'
        },
        {
          name: 'Adaptabilidade',
          description: 'Tive que me adaptar rapidamente quando percebi que meu conhecimento inicial em TypeScript era insuficiente. Assisti diversos tutoriais e li documentação para acompanhar o ritmo do projeto.'
        }
      ],
      challenges: [
        'Dificuldade inicial com conceitos avançados de TypeScript',
        'Problemas de comunicação com a equipe de backend',
        'Estimativas de tempo otimistas demais no início',
        'Dificuldade em priorizar tarefas no começo do projeto'
      ]
    },
    {
      id: 'prostock',
      title: 'ProStock - Sistema de Gestão de Estoque',
      description: (
        <>
          <p className="mb-4">
            Como líder técnico deste projeto, aprendi na pele que gerenciar pessoas é tão desafiador quanto resolver problemas de código. Assumi a liderança sem experiência prévia, o que gerou algumas situações constrangedoras no início.
          </p>
          <p>
            Nos primeiros sprints, falhei em delegar tarefas adequadamente, tentando fazer tudo sozinho. Isso me sobrecarregou e atrasou algumas entregas. Aprendi (do jeito difícil) que liderança é sobre capacitar outros, não sobre ser o melhor técnico.
          </p>
        </>
      ),
      details: [
        'Curva de aprendizado íngreme sobre Prisma ORM',
        'Dificuldades iniciais na coordenação da equipe',
        'Problemas de estimativa que causaram retrabalho',
        'Conflitos na equipe que precisei mediar',
        'Desafio de manter consistência no código com vários contribuidores',
        'Aprendizado sobre arquitetura limpa na prática'
      ],
      technologies: ['React', 'TypeScript', 'Prisma', 'REST API'],
      githubLink: 'https://github.com/EquipeSkyfall/API_2Semestre',
      softSkills: [
        {
          name: 'Humildade',
          description: 'Tive que admitir quando não sabia algo e pedir ajuda. Como líder, foi difícil mostrar vulnerabilidade, mas isso acabou criando um ambiente mais aberto.'
        },
        {
          name: 'Gestão de Conflitos',
          description: 'Mediei discussões acaloradas sobre abordagens técnicas. No início evitava conflitos, depois aprendi que debates saudáveis são produtivos.'
        },
        {
          name: 'Delegação',
          description: 'Meu instinto era microgerenciar, mas vi que isso sufocava a equipe. Aos poucos aprendi a confiar mais nos outros e delegar adequadamente.'
        }
      ],
      challenges: [
        'Insegurança inicial sobre minhas capacidades de liderança',
        'Dificuldade em dizer "não" quando sobrecarregado',
        'Problemas para alinhar expectativas com o Product Owner',
        'Equipe com níveis diferentes de experiência'
      ]
    },
    {
      id: 'estufa',
      title: 'Smart Farming - Monitoramento de Estufas',
      description: (
        <>
          <p className="mb-4">
            Este projeto parecia simples no papel, mas me ensinou que até tarefas "fáceis" podem esconder complexidades. Subestimei o desafio de criar visualizações de dados que fossem intuitivas para agricultores sem conhecimento técnico.
          </p>
          <p>
            Meu orgulho inicial ("não preciso de ajuda") me custou tempo quando fiquei travado em problemas de renderização de gráficos. Só depois de muita insistência admiti que precisava pesquisar mais e pedir orientação.
          </p>
        </>
      ),
      details: [
        'Problemas de performance com grandes conjuntos de dados',
        'Dificuldade em traduzir requisitos técnicos para linguagem simples',
        'Implementação inicial de gráficos foi ingênua e pouco eficiente',
        'Desafio de trabalhar com IoT sem hardware físico no início',
        'Aprendizado sobre Docker na marra quando tive problemas de ambiente',
        'Dificuldade em priorizar features para MVP'
      ],
      technologies: ['IoT', 'UI/UX Design', 'Data Visualization', 'Python', 'Docker'],
      githubLink: 'https://github.com/CyberScrums/Projeto-Smart-Farming',
      softSkills: [
        {
          name: 'Autocrítica',
          description: 'Precisei reconhecer quando minha abordagem inicial estava errada e pivotar. Foi humilhante admitir que protótipos iniciais não atendiam às necessidades reais.'
        },
        {
          name: 'Empatia',
          description: 'Aprendi a sair da bolha técnica e entender usuários reais. Minhas primeiras interfaces eram ótimas tecnicamente, mas confusas para os agricultores.'
        },
        {
          name: 'Persistência',
          description: 'Quase desisti quando os gráficos complexos não renderizavam corretamente, mas continuei testando abordagens até encontrar uma solução.'
        }
      ],
      challenges: [
        'Dificuldade em entender o domínio de agricultura',
        'Problemas com bibliotecas de gráficos que não escalavam',
        'Comunicação ineficiente com stakeholders não-técnicos',
        'Gerenciamento de expectativas sobre o que era possível entregar'
      ]
    }
  ];

  return (
    <section className="py-20 px-4 sm:px-6 max-w-6xl mx-auto">
      <div className="text-center mb-12 sm:mb-16">
        <div className="relative inline-block group">
          <h1 className="text-3xl sm:text-4xl md:text-5xl font-bold text-amber-400 mb-4">
            Meus Projetos
          </h1>
        </div>
        <p className="text-lg sm:text-xl text-gray-300 max-w-3xl mx-auto px-4">
          Jornadas reais com desafios, aprendizados e crescimento profissional
        </p>
      </div>

      <div className="space-y-6 sm:space-y-8 px-2 sm:px-0">
        {projects.map((project) => (
          <div
            key={project.id}
            className="bg-gray-800/50 backdrop-blur-sm border border-gray-700 rounded-xl overflow-hidden transition-all duration-300 hover:border-amber-400/30"
          >
            <div className="p-6 sm:p-8">
              <div className="flex flex-col md:flex-row md:items-start justify-between gap-6">
                <div className="flex-1">
                  <h2 className="text-xl sm:text-2xl md:text-3xl font-bold text-amber-400 mb-2">{project.title}</h2>

                  <div className="flex flex-wrap gap-2 mb-4">
                    {project.technologies.map((tech, index) => (
                      <span
                        key={index}
                        className="text-xs sm:text-sm px-3 py-1 bg-gray-700 rounded-full text-amber-300"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>

                  <div className="text-gray-300 mb-4 space-y-4 text-sm sm:text-base">
                    {typeof project.description === 'string' ? (
                      <p>{project.description}</p>
                    ) : (
                      project.description
                    )}
                  </div>

                  {activeProject === project.id && (
                    <div className="mt-6 space-y-6">
                      <div>
                        <h3 className="text-lg font-semibold text-amber-300 mb-3">Desafios e Aprendizados</h3>
                        <div className="space-y-3 pl-4 border-l-2 border-amber-400/30 text-sm sm:text-base">
                          {project.details.map((item, index) => (
                            <div key={index} className="text-gray-300">
                              <p className="mb-2">• {item}</p>
                            </div>
                          ))}
                        </div>
                      </div>

                      <div>
                        <h3 className="text-lg font-semibold text-amber-300 mb-3">Soft Skills Desenvolvidas</h3>
                        <div className="space-y-4">
                          {project.softSkills.map((skill, index) => (
                            <div key={index} className="bg-gray-700/50 p-4 rounded-lg">
                              <h4 className="font-medium text-amber-200">{skill.name}</h4>
                              <p className="text-gray-300 text-sm mt-1">{skill.description}</p>
                            </div>
                          ))}
                        </div>
                      </div>

                      {project.challenges && (
                        <div>
                          <h3 className="text-lg font-semibold text-amber-300 mb-3">Dificuldades Enfrentadas</h3>
                          <div className="space-y-3 pl-4 border-l-2 border-amber-400/30 text-sm sm:text-base">
                            {project.challenges.map((challenge, index) => (
                              <div key={index} className="text-gray-300">
                                <p className="mb-2">• {challenge}</p>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}
                </div>

                <div className="flex flex-col space-y-3 sm:space-y-4 w-full sm:w-48">
                  <button
                    onClick={() => toggleProject(project.id)}
                    className="px-4 sm:px-6 py-2 sm:py-3 bg-gradient-to-r from-amber-500/90 to-amber-600/90 rounded-lg font-medium hover:from-amber-600 hover:to-amber-700 transition-all duration-300 shadow-lg hover:shadow-xl text-sm sm:text-base"
                  >
                    {activeProject === project.id ? 'Mostrar Menos' : 'Detalhes'}
                  </button>

                  <a
                    href={project.githubLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="px-4 sm:px-6 py-2 sm:py-3 border-2 border-amber-400 text-amber-400 rounded-lg font-medium hover:bg-amber-400/10 transition-all duration-300 text-center text-sm sm:text-base"
                  >
                    Ver no GitHub
                  </a>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projetos;
